/**
 * 날짜 포맷팅 YYYY/MM/DD(요일)
 * @param {Date} value - 입력값
 * @returns {String} - 유효성 검사 결과
 */
export const formatDate = (date) => {
    const options = { year: 'numeric', month: '2-digit', day: '2-digit', weekday: 'long', locale: 'ko-KR' };
    return date.toLocaleDateString('ko-KR', options).replace(/\./g, '/');
};
