import { createRouter, createWebHistory } from "vue-router";
import Routes from "@/router/routes";
import { useUserStore } from "@/stores/userStore";
import { USER_TYPES } from "@/constants/userTypes";

const router = createRouter({
    history: createWebHistory(""),
    routes: [
        {
            path: "/",
            name: Routes.Main,
            component: () => import("@/views/Common/MainView.vue"),
        },
        {
            path: "/Admin",
            name: Routes.AdminMain,
            component: () => import("@/views/Common/AdminMain.vue"),
        },
        {
            path: "/jobs/menu",
            name: Routes.JobMenu,
            component: () => import("@/views/IndividualUser/JobMenu.vue"),
        },
        {
            path: "/jobs/application-status",
            name: Routes.JobApplicationStatus,
            component: () => import("@/views/IndividualUser/JobApplicationStatus.vue"),
        },
        {
            path: "/education-status",
            name: Routes.EducationStatus,
            component: () => import("@/views/IndividualUser/EducationStatus.vue"),
        },
        {
            path: "/resume",
            name: Routes.ResumePage,
            component: () => import("@/views/IndividualUser/ResumePage.vue"),
        },
        {
            path: "/individual-user/profile",
            name: Routes.IndividualUserProfile,
            component: () => import("@/views/IndividualUser/IndividualUserProfile.vue"),
        },
        {
            path: "/wooribound-elevation",
            name: Routes.WooriboundElevationInfo,
            component: () => import("@/views/IndividualUser/WooriboundElevationInfo.vue"),
        },
        {
            path: "/education/application",
            name: Routes.EducationApplication,
            component: () => import("@/views/IndividualUser/EducationApplication.vue"),
        },
        {
            path: "/career-revive",
            name: Routes.CareerRevive,
            component: () => import("@/views/IndividualUser/CareerRevive.vue"),
        },
        {
            path: "/new-job-search",
            name: Routes.NewJobSearch,
            component: () => import("@/views/IndividualUser/NewJobSearch.vue"),
        },
        {
            path: "/all-job-listings",
            name: Routes.AllJobListings,
            component: () => import("@/views/IndividualUser/AllJobListings.vue"),
        },
        {
            path: "/login",
            name: Routes.LoginView,
            component: () => import("@/views/Authentication/LoginView.vue"),
        },
        {
            path: "/signup",
            name: Routes.RegisterView,
            component: () => import("@/views/Authentication/RegisterView.vue"),
        },
    ],
});

router.beforeEach((to, from, next) => {
    const userStore = useUserStore();

    // 사용자 로그인 여부와 사용자 유형 확인
    if (userStore.isLoggedIn &&
        (userStore.userType === USER_TYPES.INFRA_ADMIN || userStore.userType === USER_TYPES.SERVICE_ADMIN)) {

        // 루트 경로를 admin으로 리다이렉트
        if (to.path === "/") {
            next({ path: "/Admin" }); // AdminMainView로 리다이렉트
        } else {
            next(); // 다른 경로로 이동
        }
    } else {
        next(); // 로그인하지 않은 경우, 원래 경로로 이동
    }
});

export default router;
